//** primary notify **//
var primarynotify = function(){
  $.notify(
  	{
		title: '',
		message: "Example notify",
		icon: '',
		url: '',
		target: '_blank'
	},
	{
		element: 'body',
		type: "primary",
		showProgressbar: false,
		placement: {
			from: "top",
			align: "right"
		},
		offset: 20,
		spacing: 10,
		z_index: 1031,
		delay: 3300,
		timer: 1000,
		url_target: '_blank',
		mouse_over: null,
		animate: {
			enter: 'animated fadeInDown',
			exit: 'animated fadeOutRight'
		},
		onShow: null,
		onShown: null,
		onClose: null,
		onClosed: null,
		icon_type: 'class',
});
}



